import React from 'react';
import {Route} from 'react-router-dom';

import styles from './Layout.module.css';
import Toolbar from '../../components/Navigation/Toolbar/Toolbar';
import Footer from '../../components/Navigation/Footer/Footer';
import Hero from '../../components/Hero/Hero';
import Main from '../../components/Main/Main';
import SortingAlg from '../../projects/sortingVisualizer/src/App';

const Layout = (props) => {
  return (
    <div className={styles.Layout}>
      <Toolbar />
      <Route path="/" exact component={Hero} />
      <Route path="/" exact component={Main} />
      <Route path="/projects/sorting-visualizer" exact component={SortingAlg} />
      <Route path="/" exact component={Footer} />
    </div>
  );
};

export default Layout;

/* 
Design Notes:

1. Want links to light up with differing colors depending on where we are in the router.
2. need to work on properly updating of url its not detecting proper state changes.
3. make the images into CSS designed things so that they can light up properly
4. On hover of projects it would be really slick if they could light up 
5. space out bottom of footer like I did in sorting footer
6. merge sorting properly into this project it has much overlap and needs new namming
*/
